import React, { useState } from "react";
import { ethers } from "ethers";
import EduVerify from "./EduVerify.json";
import CredentialRevocation from "./CredentialRevocation.json";  // Import ABI of new contract
import InstitutionRegistry from "./InstitutionRegistry.json";  // Import ABI for InstitutionRegistry
import AccessControl from "./AccessControl.json";  // Import ABI for AccessControl
import './App.css';

const eduVerifyContractAddress = process.env.REACT_APP_CONTRACT_ADDRESS;
const revocationContractAddress = process.env.REACT_APP_REVOCATION_CONTRACT_ADDRESS;
const institutionRegistryContractAddress = process.env.REACT_APP_INSTITUTION_REGISTRY_CONTRACT;
const accessControlContractAddress = process.env.REACT_APP_ACCESS_CONTROL_CONTRACT;

function App() {
  const [account, setAccount] = useState(null);
  const [contract, setContract] = useState(null);
  const [revocationContract, setRevocationContract] = useState(null);
  const [institutionRegistryContract, setInstitutionRegistryContract] = useState(null);
  const [accessControlContract, setAccessControlContract] = useState(null);
  const [form, setForm] = useState({
    student: "",
    srn: "",
    name: "",
    course: "",
    institution: ""
  });

  const [viewAddress, setViewAddress] = useState("");
  const [credentialInfo, setCredentialInfo] = useState(null);

  const connectWallet = async () => {
    if (window.ethereum) {
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      await provider.send("eth_requestAccounts", []);
      const signer = provider.getSigner();
      const userAddress = await signer.getAddress();
      setAccount(userAddress);

      const eduVerify = new ethers.Contract(eduVerifyContractAddress, EduVerify.abi, signer);
      setContract(eduVerify);

      const revocation = new ethers.Contract(revocationContractAddress, CredentialRevocation.abi, signer);
      setRevocationContract(revocation);

      const institutionRegistry = new ethers.Contract(institutionRegistryContractAddress, InstitutionRegistry.abi, signer);
      setInstitutionRegistryContract(institutionRegistry);

      const accessControl = new ethers.Contract(accessControlContractAddress, AccessControl.abi, signer);
      setAccessControlContract(accessControl);

      console.log("Wallet connected:", userAddress);
    } else {
      alert("Please install MetaMask");
    }
  };

  // Handle form changes
  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const issueCredential = async () => {
    if (!contract) return alert("Connect wallet first!");
  
    try {
      const tx = await contract.issueCredential(
        form.student,
        form.srn,
        form.name,
        form.course,
        form.institution
      );
  
      await tx.wait();
      alert("Credential issued successfully!");
    } catch (error) {
      if (error.message.includes("Invalid SRN")) {
        alert("SRN must start with 'PES2UG22'");
      } else {
        console.error("Error issuing credential:", error);
        alert("Error issuing credential. Check console for details.");
      }
    }
  };

  const revokeCredential = async () => {
    if (!contract) return alert("Connect wallet first!");

    try {
      const tx = await contract.revokeCredential(form.student);
      await tx.wait();
      alert("Credential revoked!");
    } catch (error) {
      console.error("Error revoking credential:", error);
    }
  };

  // Revoke credential using second contract
  const revokeUsingNewContract = async () => {
    if (!revocationContract) return alert("Connect wallet first!");

    try {
      const tx = await revocationContract.revokeCredential(form.student);  // Use the new contract
      await tx.wait();
      alert("Credential revoked via new contract!");
    } catch (error) {
      console.error("Error revoking credential with new contract:", error);
    }
  };

  const addInstitution = async () => {
    if (!institutionRegistryContract) return alert("Connect wallet first!");

    try {
      const tx = await institutionRegistryContract.addInstitution(form.institution);
      await tx.wait();
      alert("Institution added successfully!");
    } catch (error) {
      console.error("Error adding institution:", error);
      alert("Error adding institution.");
    }
  };

  const grantAdminRole = async () => {
    if (!accessControlContract) return alert("Connect wallet first!");

    try {
      const tx = await accessControlContract.grantAdminRole(form.student);
      await tx.wait();
      alert("Admin role granted!");
    } catch (error) {
      console.error("Error granting admin role:", error);
      alert("Error granting admin role.");
    }
  };

  const revokeAdminRole = async () => {
    if (!accessControlContract) return alert("Connect wallet first!");

    try {
      const tx = await accessControlContract.revokeAdminRole(form.student);
      await tx.wait();
      alert("Admin role revoked!");
    } catch (error) {
      console.error("Error revoking admin role:", error);
      alert("Error revoking admin role.");
    }
  };

  const viewCredential = async () => {
    if (!contract) return alert("Connect wallet first!");

    try {
      const cred = await contract.getCredential(viewAddress);
      setCredentialInfo(cred);
    } catch (error) {
      console.error("Error viewing credential:", error);
    }
  };

  return (
    <div className="container">
      <h2>EduVerify - Credential Management</h2>
      <button onClick={connectWallet}>Connect Wallet</button>

      <h3>Issue Credential</h3>
      <input name="student" placeholder="Student Address" onChange={handleChange} /><br />
      <input name="srn" placeholder="SRN" onChange={handleChange} /><br />
      <input name="name" placeholder="Student Name" onChange={handleChange} /><br />
      <input name="course" placeholder="Course" onChange={handleChange} /><br />
      <input name="institution" placeholder="Institution" onChange={handleChange} /><br />
      <button onClick={issueCredential}>Issue</button>
      <button onClick={revokeCredential}>Revoke</button>

      <h3>Revoke Credential via New Contract</h3>
      <button onClick={revokeUsingNewContract}>Revoke with New Contract</button>

      <h3>Manage Institutions and Admin</h3>
      <button onClick={addInstitution}>Add Institution</button>
      <button onClick={grantAdminRole}>Grant Admin Role</button>
      <button onClick={revokeAdminRole}>Revoke Admin Role</button>

      <h3>View Credential</h3>
      <input
        placeholder="Student Address"
        value={viewAddress}
        onChange={(e) => setViewAddress(e.target.value)}
      />
      <button onClick={viewCredential}>View</button>

      {credentialInfo && (
        <div style={{ marginTop: "20px" }}>
          <h4>Credential Info:</h4>
          <p><strong>Name:</strong> {credentialInfo.name}</p>
          <p><strong>SRN:</strong> {credentialInfo.srn}</p>
          <p><strong>Course:</strong> {credentialInfo.course}</p>
          <p><strong>Institution:</strong> {credentialInfo.institution}</p>
          <p><strong>Issued:</strong> {new Date(credentialInfo.issueDate * 1000).toLocaleString()}</p>
          <p><strong>Valid:</strong> {credentialInfo.isValid ? "Yes" : "No"}</p>
        </div>
      )}
    </div>
  );
}

export default App;
