import React, { useState } from "react";
import { ethers } from "ethers";
import EduVerify from "./EduVerify.json"; // Ensure this file is in frontend/src

const contractAddress = process.env.REACT_APP_CONTRACT_ADDRESS;

function App() {
  const [account, setAccount] = useState(null);
  const [contract, setContract] = useState(null);

  const [student, setStudent] = useState("");
  const [name, setName] = useState("");
  const [course, setCourse] = useState("");
  const [institution, setInstitution] = useState("");

  const connectWallet = async () => {
    if (window.ethereum) {
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      await provider.send("eth_requestAccounts", []);
      const signer = provider.getSigner();
      const userAddress = await signer.getAddress();
      setAccount(userAddress);

      const eduVerifyContract = new ethers.Contract(contractAddress, EduVerify.abi, signer);
      setContract(eduVerifyContract);

      console.log("Wallet connected:", userAddress);
    } else {
      alert("Please install MetaMask");
    }
  };

  const issueCredential = async () => {
    if (!contract) {
      alert("Please connect your wallet first!");
      return;
    }

    if (!ethers.utils.isAddress(student)) {
      alert("Please enter a valid Ethereum address for the student.");
      return;
    }

    try {
      const tx = await contract.issueCredential(student, name, course, institution);
      await tx.wait();
      alert("Credential issued successfully!");
    } catch (error) {
      console.error("Error issuing credential:", error);
      alert("Transaction failed. See console for details.");
    }
  };

  return (
    <div style={{ padding: "20px", fontFamily: "Arial" }}>
      <h1>EduVerify DApp</h1>
      <button onClick={connectWallet}>
        {account ? `Connected: ${account.slice(0, 6)}...${account.slice(-4)}` : "Connect Wallet"}
      </button>

      <div style={{ marginTop: "20px" }}>
        <input
          type="text"
          placeholder="Student Address (0x...)"
          value={student}
          onChange={(e) => setStudent(e.target.value)}
          style={{ display: "block", marginBottom: "10px", width: "300px" }}
        />
        <input
          type="text"
          placeholder="Student Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          style={{ display: "block", marginBottom: "10px", width: "300px" }}
        />
        <input
          type="text"
          placeholder="Course"
          value={course}
          onChange={(e) => setCourse(e.target.value)}
          style={{ display: "block", marginBottom: "10px", width: "300px" }}
        />
        <input
          type="text"
          placeholder="Institution"
          value={institution}
          onChange={(e) => setInstitution(e.target.value)}
          style={{ display: "block", marginBottom: "10px", width: "300px" }}
        />
        <button onClick={issueCredential}>Issue Credential</button>
      </div>
    </div>
  );
}

export default App;
