import React, { useState } from "react";
import { ethers } from "ethers";
import EduVerify from "./EduVerify.json";
import './App.css';

const contractAddress = process.env.REACT_APP_CONTRACT_ADDRESS;

function App() {
  const [account, setAccount] = useState(null);
  const [contract, setContract] = useState(null);
  const [form, setForm] = useState({
    student: "",
    srn: "",
    name: "",
    course: "",
    institution: ""
  });
  const [viewAddress, setViewAddress] = useState("");
  const [credentialInfo, setCredentialInfo] = useState(null);

  const connectWallet = async () => {
    if (window.ethereum) {
      const provider = new ethers.providers.Web3Provider(window.ethereum);
      await provider.send("eth_requestAccounts", []);
      const signer = provider.getSigner();
      const userAddress = await signer.getAddress();
      setAccount(userAddress);

      const eduVerifyContract = new ethers.Contract(contractAddress, EduVerify.abi, signer);
      setContract(eduVerifyContract);

      console.log("Wallet connected:", userAddress);
    } else {
      alert("Please install MetaMask");
    }
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const isValidSRN = (srn) => {
    const srnPattern = /^PES2UG\d{2}CS\d{3}$/;
    return srnPattern.test(srn);
  };

  const issueCredential = async () => {
    if (!contract) return alert("Connect wallet first!");

    if (!isValidSRN(form.srn)) {
      alert("Invalid SRN format. It should look like: PES2UG22CS198");
      return;
    }

    try {
      console.log("Params being sent to contract:");
      console.log("recipient:", recipient);
      console.log("srn:", srn);
      console.log("name:", name);
      console.log("dept:", dept);
      console.log("university:", university);
      console.log("course:", course);
      console.log("issuer:", issuer);

      // Pass the connected wallet address (account) as the student address
      const tx = await contract.issueCredential(
        account,  // Use the connected account here
        form.srn,
        form.name,
        form.course,
        form.institution,
        {
          gasLimit: 2000000// Set a higher gas limit (adjust based on the complexity of your transaction)
        }
      );
      await tx.wait();
      alert("Credential issued successfully!");
    } catch (error) {
      console.error("Error issuing credential:", error);
      alert("Error issuing credential. See console for details.");
    }
    
  };

  const revokeCredential = async () => {
    if (!contract) return alert("Connect wallet first!");

    try {
      const tx = await contract.revokeCredential(form.student);
      await tx.wait();
      alert("Credential revoked!");
    } catch (error) {
      console.error("Error revoking credential:", error);
    }
  };

  const viewCredential = async () => {
    if (!contract) return alert("Connect wallet first!");

    try {
      const cred = await contract.getCredential(viewAddress);
      setCredentialInfo(cred);
    } catch (error) {
      console.error("Error viewing credential:", error);
    }
  };

  return (
    <div className="container">
      <h2>EduVerify - Credential Management</h2>
      <button onClick={connectWallet}>Connect Wallet</button>

      <h3>Issue Credential</h3>
      <input name="student" placeholder="Student Address" onChange={handleChange} /><br />
      <input name="srn" placeholder="SRN (e.g., PES2UG22CS198)" onChange={handleChange} /><br />
      <input name="name" placeholder="Student Name" onChange={handleChange} /><br />
      <input name="course" placeholder="Course" onChange={handleChange} /><br />
      <input name="institution" placeholder="Institution" onChange={handleChange} /><br />
      <button onClick={issueCredential}>Issue</button>
      <button onClick={revokeCredential}>Revoke</button>

      <h3>View Credential</h3>
      <input
        placeholder="Student Address"
        value={viewAddress}
        onChange={(e) => setViewAddress(e.target.value)}
      />
      <button onClick={viewCredential}>View</button>

      {credentialInfo && (
        <div style={{ marginTop: "20px" }}>
          <h4>Credential Info:</h4>
          <p><strong>Name:</strong> {credentialInfo.name}</p>
          <p><strong>SRN:</strong> {credentialInfo.srn}</p>
          <p><strong>Course:</strong> {credentialInfo.course}</p>
          <p><strong>Institution:</strong> {credentialInfo.institution}</p>
          <p><strong>Issued:</strong> {new Date(credentialInfo.issueDate * 1000).toLocaleString()}</p>
          <p><strong>Valid:</strong> {credentialInfo.isValid ? "Yes" : "No"}</p>
        </div>
      )}
    </div>
  );
}

export default App;
