import { useState } from "react";
import { ethers } from "ethers";
import EduVerifyABI from "./EduVerify.json"; // Place ABI here

const contractAddress = process.env.REACT_APP_CONTRACT_ADDRESS;

function App() {
  const [account, setAccount] = useState(null);
  const [provider, setProvider] = useState(null);
  const [contract, setContract] = useState(null);

  const connectWallet = async () => {
    const prov = new ethers.providers.Web3Provider(window.ethereum);
    await prov.send("eth_requestAccounts", []);
    const signer = prov.getSigner();
    const userAddress = await signer.getAddress();

    const eduVerifyContract = new ethers.Contract(contractAddress, EduVerifyABI, signer);

    setAccount(userAddress);
    setProvider(prov);
    setContract(eduVerifyContract);
  };

  const issueCredential = async () => {
    const tx = await contract.issueCredential(
      "0xStudentAddress", // recipient student
      "Alice",            // name
      "B.Tech",           // course
      "Your University"   // institution
    );
    await tx.wait();
    alert("Credential Issued!");
  };

  return (
    <div>
      <button onClick={connectWallet}>Connect Wallet</button>
      {account && <p>Connected as: {account}</p>}
      <button onClick={issueCredential}>Issue Credential</button>
    </div>
  );
}

export default App;
