import { useState, useEffect } from "react";
import { ethers } from "ethers";
import EduVerifyABI from "./abis/EduVerifyABI.json"; // Import ABI

const contractAddress = "0x5FbDB2315678afecb367f032d93F642f64180aa3"; // Replace with deployed address

function App() {
  const [account, setAccount] = useState("");
  const [contract, setContract] = useState(null);
  const [credential, setCredential] = useState(null);

  useEffect(() => {
    async function loadBlockchainData() {
      if (window.ethereum) {
        try {
          // Request account access
          const accounts = await window.ethereum.request({ method: "eth_requestAccounts" });
          const provider = new ethers.providers.Web3Provider(window.ethereum);
          const signer = provider.getSigner();
          const contractInstance = new ethers.Contract(contractAddress, EduVerifyABI, signer);

          setAccount(accounts[0]); // Set the first account
          setContract(contractInstance);
        } catch (error) {
          console.error("Error loading blockchain data:", error);
        }
      } else {
        console.error("MetaMask is not installed. Please install MetaMask.");
      }
    }

    loadBlockchainData();

    // Listen for account change
    if (window.ethereum) {
      window.ethereum.on("accountsChanged", (accounts) => {
        setAccount(accounts.length > 0 ? accounts[0] : "Not connected");
      });
    }
  }, []);

  const verifyCredential = async () => {
    if (contract) {
      try {
        const data = await contract.verifyCredential(account);
        setCredential(data);
      } catch (error) {
        console.error("Error verifying credential:", error);
      }
    } else {
      console.error("Contract is not loaded.");
    }
  };

  return (
    <div>
      <h1>EduVerify: Blockchain Student Credentials</h1>
      <p><strong>Connected Account:</strong> {account || "Not connected"}</p>
      <button onClick={verifyCredential}>Verify Credential</button>
      {credential && (
        <div>
          <p><strong>Name:</strong> {credential[0] || "N/A"}</p>
          <p><strong>Course:</strong> {credential[1] || "N/A"}</p>
          <p><strong>Institution:</strong> {credential[2] || "N/A"}</p>
          <p><strong>Issued Date:</strong> {credential[3] ? new Date(credential[3] * 1000).toLocaleDateString() : "N/A"}</p>
          <p><strong>Status:</strong> {credential[4] ? "Valid" : "Revoked"}</p>
        </div>
      )}
    </div>
  );
}

export default App;
