import { useState, useEffect } from "react";
import { ethers } from "ethers";
import EduVerifyABI from "./EduVerifyABI.json"; // Import ABI

const contractAddress = "0xYourContractAddress"; // Replace with deployed address

function App() {
  const [account, setAccount] = useState("");
  const [contract, setContract] = useState(null);
  const [credential, setCredential] = useState(null);

  useEffect(() => {
    async function loadBlockchainData() {
      if (window.ethereum) {
        const provider = new ethers.providers.Web3Provider(window.ethereum);
        const signer = provider.getSigner();
        const contractInstance = new ethers.Contract(contractAddress, EduVerifyABI, signer);

        setAccount(await signer.getAddress());
        setContract(contractInstance);
      }
    }
    loadBlockchainData();
  }, []);

  const verifyCredential = async () => {
    if (contract) {
      const data = await contract.verifyCredential(account);
      setCredential(data);
    }
  };

  return (
    <div>
      <h1>EduVerify: Blockchain Student Credentials</h1>
      <button onClick={verifyCredential}>Verify Credential</button>
      {credential && (
        <div>
          <p>Name: {credential[0]}</p>
          <p>Course: {credential[1]}</p>
          <p>Institution: {credential[2]}</p>
          <p>Issued Date: {new Date(credential[3] * 1000).toLocaleDateString()}</p>
          <p>Status: {credential[4] ? "Valid" : "Revoked"}</p>
        </div>
      )}
    </div>
  );
}

export default App;
